import React, {useRef, useEffect, useState} from 'react'
import Hls from 'hls.js'
import OverlayEditor from './OverlayEditor'

export default function Player({hlsUrl}){
  const videoRef = useRef(null)
  const [overlays, setOverlays] = useState([])

  useEffect(()=>{
    const v = videoRef.current
    if (!v) return
    if (Hls.isSupported()){
      const hls = new Hls()
      hls.loadSource(hlsUrl)
      hls.attachMedia(v)
    } else if (v.canPlayType('application/vnd.apple.mpegurl')){
      v.src = hlsUrl
    }
  }, [hlsUrl])

  useEffect(()=>{
    fetch('/api/overlays')
      .then(r=>r.json())
      .then(setOverlays)
      .catch(()=>setOverlays([]))
  }, [])

  return (
    <div className="player-wrapper">
      <div className="video-container" style={{position:'relative'}}>
        <video ref={videoRef} controls style={{width: '100%'}} />
        {overlays.map(o=> (
  <Rnd key={o._id}
       size={{width: o.width, height: o.height}}
       position={{x: o.x, y: o.y}}
       onDragStop={(e, d)=> saveItem(o._id, {...o, x: d.x, y: d.y})}
       onResizeStop={(e, direction, ref, delta, position)=> saveItem(o._id, {...o, width: parseInt(ref.style.width), height: parseInt(ref.style.height), x: position.x, y: position.y})}
       bounds="parent"
       style={{border: '1px dashed #ddd', background: 'rgba(0,0,0,0.4)', color:'white', fontSize:'20px'}}>
    {o.type === 'text' ? (
       <textarea
         value={o.content}
         onChange={(e)=> saveItem(o._id, {...o, content: e.target.value})}
         style={{width:'100%', height:'100%', background:'transparent', color:'white', border:'none', resize:'none'}} 
       />
    ) : (
       <img src={o.content} alt="overlay" style={{width:'100%',height:'100%'}} />
    )}
  </Rnd>
))}

      </div>
      {/* Pass overlays state and setter to OverlayEditor */}
      <OverlayEditor overlays={overlays} setOverlays={setOverlays} />
    </div>
  )
}
