import React from 'react';
import { Rnd } from 'react-rnd';

export default function OverlayEditor({ overlays, setOverlays }) {

  const addText = async () => {
    try {
      const payload = { name: 'New text', type: 'text', content: 'Hello', x: 10, y: 10, width: 150, height: 40 };
      const res = await fetch('/api/overlays', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!res.ok) throw new Error('Failed to add overlay');
      const data = await res.json();
      setOverlays(prev => [...prev, data]);
    } catch (err) {
      console.error('Error adding overlay:', err);
    }
  };

  const saveItem = async (id, updates) => {
    try {
      const exists = overlays.find(o => o._id === id);
      if (!exists) return; // skip if overlay deleted

      const { _id, ...payload } = updates; // remove _id
      const res = await fetch(`/api/overlays/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!res.ok) throw new Error(`Failed to save overlay: ${res.statusText}`);
      const data = await res.json();
      setOverlays(prev => prev.map(it => it._id === id ? data : it));
    } catch (err) {
      console.error('Error saving overlay:', err);
    }
  };

  const deleteItem = async (id) => {
    try {
      await fetch(`/api/overlays/${id}`, { method: 'DELETE' });
      setOverlays(prev => prev.filter(it => it._id !== id));
    } catch (err) {
      console.error('Error deleting overlay:', err);
    }
  };

  return (
    <div className="overlay-editor" style={{ marginTop: '10px' }}>
      <button onClick={addText}>Add Text</button>
      <div className="editor-canvas" style={{ position: 'relative', width: '100%', height: '300px', border: '1px solid #ccc', marginTop: '10px' }}>
        {overlays.map(it => (
          <Rnd
            key={it._id}
            size={{ width: it.width, height: it.height }}
            position={{ x: it.x, y: it.y }}
            onDragStop={(e, d) => saveItem(it._id, { ...it, x: d.x, y: d.y })}
            onResizeStop={(e, direction, ref, delta, position) =>
              saveItem(it._id, {
                ...it,
                width: parseInt(ref.style.width),
                height: parseInt(ref.style.height),
                x: position.x,
                y: position.y,
              })
            }
            bounds="parent"
            style={{ border: '1px dashed #ddd', background: 'rgba(255,255,255,0.6)' }}
          >
            {it.type === 'text' ? (
              <div style={{ padding: 4 }}>
                <textarea
                  value={it.content}
                  onChange={e =>
                    setOverlays(prev => prev.map(o => o._id === it._id ? { ...o, content: e.target.value } : o))
                  }
                  onBlur={e => saveItem(it._id, { ...it, content: e.target.value })}
                  style={{ width: '100%', height: '100%', resize: 'none', background: 'transparent', color: 'black', border: '1px solid #ccc', padding: '2px' }}
                />
                <button
                  onClick={e => {
                    e.stopPropagation();
                    e.preventDefault(); // prevents onBlur save during delete
                    deleteItem(it._id);
                  }}
                >
                  Delete
                </button>
              </div>
            ) : (
              <img src={it.content} alt="overlay" style={{ width: '100%', height: '100%' }} />
            )}
          </Rnd>
        ))}
      </div>
    </div>
  );
}
