import React, { useEffect, useState } from "react";
import axios from "axios";
import { Rnd } from "react-rnd";

export default function Player() {
  const [overlays, setOverlays] = useState([]);
  const [newText, setNewText] = useState("");

  // Load overlays from backend
  useEffect(() => {
    axios.get("http://localhost:5000/api/overlays")
      .then(res => setOverlays(res.data))
      .catch(err => console.error(err));
  }, []);

  // Add new overlay
  const addOverlay = async () => {
    if (!newText.trim()) return;
    const res = await axios.post("http://localhost:5000/api/overlays", {
      type: "text",
      content: newText,
      x: 50,
      y: 50,
      width: 200,
      height: 50
    });
    setOverlays([...overlays, res.data]);
    setNewText("");
  };

  // Update overlay
  const saveOverlay = async (id, updates) => {
    const res = await axios.put(`http://localhost:5000/api/overlays/${id}`, updates);
    setOverlays(prev => prev.map(o => o._id === id ? res.data : o));
  };

  // Delete overlay
  const deleteOverlay = async (id) => {
    await axios.delete(`http://localhost:5000/api/overlays/${id}`);
    setOverlays(prev => prev.filter(o => o._id !== id));
  };

  return (
    <div style={{ position: "relative", width: "640px", height: "360px" }}>
      {/* Video */}
      <video
        controls
        width="640"
        height="360"
        src="http://localhost:5000/hls/stream.m3u8"
      ></video>

      {/* Overlays */}
      {overlays.map(o => (
        <Rnd
          key={o._id}
          size={{ width: o.width, height: o.height }}
          position={{ x: o.x, y: o.y }}
          onDragStop={(e, d) => saveOverlay(o._id, { ...o, x: d.x, y: d.y })}
          onResizeStop={(e, dir, ref, delta, pos) => saveOverlay(o._id, {
            ...o,
            width: parseInt(ref.style.width),
            height: parseInt(ref.style.height),
            x: pos.x,
            y: pos.y
          })}
          bounds="parent"
          style={{ border: "1px dashed #ddd", background: "rgba(0,0,0,0.5)", color: "white" }}
        >
          {o.type === "text" ? (
            <div style={{ width: "100%", height: "100%" }}>
              <textarea
                value={o.content}
                onChange={e => saveOverlay(o._id, { ...o, content: e.target.value })}
                style={{ width: "100%", height: "70%", background: "transparent", color: "white", border: "none", resize: "none" }}
              />
              <button onClick={() => deleteOverlay(o._id)}>Delete</button>
            </div>
          ) : (
            <img src={o.content} alt="overlay" style={{ width: "100%", height: "100%" }} />
          )}
        </Rnd>
      ))}

      {/* Add overlay */}
      <div style={{ marginTop: "10px" }}>
        <input
          type="text"
          placeholder="Enter overlay text"
          value={newText}
          onChange={e => setNewText(e.target.value)}
        />
        <button onClick={addOverlay}>Add Overlay</button>
      </div>
    </div>
  );
}
