import React, { useEffect, useState } from "react";
import axios from "axios";
import { Rnd } from "react-rnd";

export default function Player() {
  const [overlays, setOverlays] = useState([]);
  const [newText, setNewText] = useState("");

  // Load overlays
  useEffect(() => {
    axios.get("http://localhost:5000/api/overlays")
      .then(res => setOverlays(res.data))
      .catch(err => console.error(err));
  }, []);

  // Add new overlay
  const addOverlay = async () => {
    if (!newText.trim()) return;
    const payload = {
      type: "text",
      content: newText,
      x: 50,
      y: 50,
      width: 200,
      height: 50
    };
    try {
      const res = await axios.post("http://localhost:5000/api/overlays", payload);
      setOverlays(prev => [...prev, res.data]);
      setNewText("");
    } catch (err) {
      console.error(err);
    }
  };

  // Save overlay
  const saveOverlay = async (id, updates) => {
    const { _id, ...payload } = updates;
    try {
      const res = await axios.put(`http://localhost:5000/api/overlays/${id}`, payload);
      setOverlays(prev => prev.map(o => o._id === id ? res.data : o));
    } catch (err) {
      console.error(err);
    }
  };

  // Delete overlay
  const deleteOverlay = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/overlays/${id}`);
      setOverlays(prev => prev.filter(o => o._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ width: "640px", margin: "0 auto", position: "relative" }}>
      {/* Livestream video */}
      <video
        controls
        width="640"
        height="360"
        src="http://localhost:5000/hls/stream.m3u8"
        style={{ display: "block" }}
      />

      {/* Overlays on top of video */}
      {overlays.map(o => (
        <Rnd
          key={o._id}
          size={{ width: o.width, height: o.height }}
          position={{ x: o.x, y: o.y }}
          onDragStop={(e, d) => saveOverlay(o._id, { ...o, x: d.x, y: d.y })}
          onResizeStop={(e, dir, ref, delta, pos) =>
            saveOverlay(o._id, {
              ...o,
              width: parseInt(ref.style.width),
              height: parseInt(ref.style.height),
              x: pos.x,
              y: pos.y
            })
          }
          bounds="parent"
          style={{
            position: "absolute",
            border: "1px dashed #ddd",
            background: "rgba(255,255,255,0.5)",
          }}
        >
          {o.type === "text" ? (
            <div style={{ width: "100%", height: "100%" }}>
              <textarea
                value={o.content}
                onChange={e =>
                  setOverlays(prev => prev.map(obj => obj._id === o._id ? { ...obj, content: e.target.value } : obj))
                }
                onBlur={e => saveOverlay(o._id, { ...o, content: e.target.value })}
                style={{
                  width: "100%",
                  height: "100%",
                  resize: "none",
                  border: "none",
                  background: "transparent",
                  color: "black",
                  padding: "2px"
                }}
              />
              <button
                onClick={e => {
                  e.stopPropagation();
                  deleteOverlay(o._id);
                }}
                style={{ marginTop: "2px" }}
              >
                Delete
              </button>
            </div>
          ) : (
            <img src={o.content} alt="overlay" style={{ width: "100%", height: "100%" }} />
          )}
        </Rnd>
      ))}

      {/* Add new text overlay */}
      <div style={{ marginTop: "10px" }}>
        <input
          type="text"
          placeholder="Enter overlay text"
          value={newText}
          onChange={e => setNewText(e.target.value)}
        />
        <button onClick={addOverlay}>Add Text</button>
      </div>
    </div>
  );
}
