import React, {useRef, useEffect, useState} from 'react'
import Hls from 'hls.js'
import OverlayEditor from './OverlayEditor'

export default function Player({hlsUrl}){
  const videoRef = useRef(null)
  const [overlays, setOverlays] = useState([])

  useEffect(()=>{
    const v = videoRef.current
    if (!v) return
    if (Hls.isSupported()){
      const hls = new Hls()
      hls.loadSource(hlsUrl)
      hls.attachMedia(v)
    } else if (v.canPlayType('application/vnd.apple.mpegurl')){
      v.src = hlsUrl
    }
  }, [hlsUrl])

  useEffect(()=>{
    fetch('/api/overlays')
      .then(r=>r.json())
      .then(setOverlays)
      .catch(()=>setOverlays([]))
  }, [])

  return (
    <div className="player-wrapper">
      <div className="video-container" style={{position:'relative'}}>
        <video ref={videoRef} controls style={{width: '100%'}} />
        {overlays.map(o=> (
          <div key={o._id}
               className="overlay"
               style={{
                 position:'absolute',
                 left: o.x + 'px',
                 top: o.y + 'px',
                 width: o.width + 'px',
                 height: o.height + 'px',
                 pointerEvents: 'none',
                 color: 'white',
                 fontSize: '20px',
                 background: o.type==='text' ? 'rgba(0,0,0,0.4)' : 'transparent',
                 padding: o.type==='text' ? '4px' : '0'
               }}>
            {o.type === 'text' ? o.content : <img src={o.content} alt="overlay" style={{width:'100%',height:'100%'}} />}
          </div>
        ))}
      </div>
      {/* Pass overlays state and setter to OverlayEditor */}
      <OverlayEditor overlays={overlays} setOverlays={setOverlays} />
    </div>
  )
}
