import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Player() {
  const [overlays, setOverlays] = useState([]);
  const [newText, setNewText] = useState("");

  // Load overlays from backend
  useEffect(() => {
    axios.get("http://localhost:5000/api/overlays")
      .then(res => setOverlays(res.data))
      .catch(err => console.error(err));
  }, []);

  // Add new overlay (text)
  const addOverlay = async () => {
    if (!newText.trim()) return;
    const res = await axios.post("http://localhost:5000/api/overlays", {
      type: "text",
      content: newText,
      x: 100,
      y: 50,
      width: 200,
      height: 50
    });
    setOverlays([...overlays, res.data]);
    setNewText("");
  };

  return (
    <div style={{ position: "relative", width: "640px", height: "360px" }}>
      {/* Video */}
      <video
        controls
        width="640"
        height="360"
        src="http://localhost:5000/hls/stream.m3u8"
      ></video>

      {/* Render overlays */}
      {overlays.map((o) => (
        <div
          key={o._id}
          style={{
            position: "absolute",
            top: o.y,
            left: o.x,
            width: o.width,
            height: o.height,
            color: "white",
            fontWeight: "bold",
            background: "rgba(0,0,0,0.5)",
            padding: "5px",
            overflow: "hidden"
          }}
        >
          {o.type === "text" ? o.content : <img src={o.content} alt="logo" style={{ width: "100%", height: "100%" }} />}
        </div>
      ))}

      {/* Add overlay input */}
      <div style={{ marginTop: "10px" }}>
        <input
          type="text"
          placeholder="Enter overlay text"
          value={newText}
          onChange={(e) => setNewText(e.target.value)}
        />
        <button onClick={addOverlay}>Add Overlay</button>
      </div>
    </div>
  );
}
