import React from 'react'
import { Rnd } from 'react-rnd'

export default function OverlayEditor({overlays, setOverlays}){

  const addText = async ()=>{
    const payload = { name: 'New text', type: 'text', content: 'Hello', x: 10, y: 10, width: 150, height: 40 }
    const res = await fetch('/api/overlays',{
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify(payload)
    })
    const data = await res.json()
    setOverlays(prev=>[...prev, data])
  }

  const saveItem = async (id, updates)=>{
    const res = await fetch(`/api/overlays/${id}`,{
      method:'PUT',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify(updates)
    })
    const data = await res.json()
    setOverlays(prev => prev.map(it=> it._id===id?data:it))
  }

  const deleteItem = async (id)=>{
    await fetch(`/api/overlays/${id}`,{method:'DELETE'})
    setOverlays(prev => prev.filter(it=>it._id!==id))
  }

  return (
    <div className="overlay-editor" style={{marginTop:'10px'}}>
      <button onClick={addText}>Add Text</button>
      <div className="editor-canvas" style={{position:'relative', width:'100%', height:'300px', border:'1px solid #ccc', marginTop:'10px'}}>
        {overlays.map(it=> (
          <Rnd key={it._id}
               size={{width: it.width, height: it.height}}
               position={{x: it.x, y: it.y}}
               onDragStop={(e, d)=> saveItem(it._id, {...it, x: d.x, y: d.y})}
               onResizeStop={(e, direction, ref, delta, position)=> saveItem(it._id, {...it, width: parseInt(ref.style.width), height: parseInt(ref.style.height), x: position.x, y: position.y})}
               bounds="parent"
               style={{border: '1px dashed #ddd', background: 'rgba(255,255,255,0.6)'}}>
            {it.type === 'text' ? (
              <div style={{padding:4}}>
                <textarea value={it.content} 
                          onChange={(e)=> saveItem(it._id, {...it, content: e.target.value})} 
                          style={{width:'100%', height:'100%'}} />
                <button onClick={()=> deleteItem(it._id)}>Delete</button>
              </div>
            ) : (
              <img src={it.content} alt="overlay" style={{width:'100%',height:'100%'}} />
            )}
          </Rnd>
        ))}
      </div>
    </div>
  )
}
