import React, {useRef, useEffect, useState} from 'react'
import Hls from 'hls.js'
import OverlayEditor from './OverlayEditor'

export default function Player({hlsUrl}){
  const videoRef = useRef(null)
  const [overlays, setOverlays] = useState([])

  useEffect(()=>{
    const v = videoRef.current
    if (!v) return
    if (Hls.isSupported()){
      const hls = new Hls()
      hls.loadSource(hlsUrl)
      hls.attachMedia(v)
    } else if (v.canPlayType('application/vnd.apple.mpegurl')){
      v.src = hlsUrl
    }
  }, [hlsUrl])

  useEffect(()=>{
    fetch('/api/overlays')
      .then(r=>r.json())
      .then(setOverlays)
      .catch(()=>setOverlays([]))
  }, [])

  return (
    <div className="player-wrapper">
      <div className="video-container" style={{position:'relative'}}>
  <video ref={videoRef} controls style={{width: '100%'}} />

  {overlays.map(o=> (
    <Rnd key={o._id}
         size={{
           width: videoRef.current?.clientWidth,
           height: videoRef.current?.clientHeight
         }}
         position={{x:0, y:0}}
         enableResizing={false} // overlay size fixed to video
         dragAxis="none"        // no dragging
         bounds="parent"
         style={{border:'none'}}>
      {o.type==='text' ? (
        <textarea
          value={o.content}
          onChange={(e)=> saveItem(o._id, {...o, content: e.target.value})}
          style={{
            width:'100%',
            height:'100%',
            background:'transparent',
            color:'white',
            fontSize:'24px',
            border:'none',
            resize:'none'
          }}
        />
      ) : (
        <img src={o.content} alt="overlay" style={{width:'100%',height:'100%'}} />
      )}
    </Rnd>
  ))}
</div>

      {/* Pass overlays state and setter to OverlayEditor */}
      <OverlayEditor overlays={overlays} setOverlays={setOverlays} />
    </div>
  )
}
